 if (!caseSensitive) {
 filter = filter.toLowerCase();
 data = data.toLowerCase();
 }

 return data.indexOf(filter) !== -1;
 }

 public static moveArrayItem(array: unknown[], oldIndex: number, newIndex: number) {
 if (oldIndex < 0) {
 return;
 }

 if (newIndex < 0) {
 newIndex += array.length;
 }

 if (newIndex >= array.length) {
