 angular.json eslint.config.js gojs-angular-basic.report.json LICENSE.md lint.after.all lint.after.recommended lint.before.all lint.before.recommended node_modules package.json package-lock.json README.md src tsconfig.app.json tsconfig.json @param changedPropAndVal An object with 2 entries: "prop" (the node data prop changed), and "newVal" (the value the user entered in the inspector <input>)
 */
 public handleInspectorChange(changedPropAndVal) {
 const path = changedPropAndVal.prop;
 const value = changedPropAndVal.newVal;

 this.state = produce(this.state, (draft) => {
 var data = draft.selectedNodeData;
 data[path] = value;
 const key = data.key;
 const idx = draft.diagramNodeData.findIndex((nd) => nd.key == key);
 if (idx >= 0) {
 draft.diagramNodeData[idx] = data;
 draft.skipsDiagramUpdate = false; // we need to sync GoJS data with this new app state, so do not skips Diagram update
 }
 });
 }
}

import { Input } from '@angular/core';
import { Component, EventEmitter, Output } from '@angular/core';
import { FormsModule } from '@angular/forms';

@Component({
 selector: 'app-inspector-row',
 templateUrl: './inspector-row.component.html',
 imports: [FormsModule],
})
export class InspectorRowComponent {
 @Input()
 public id: string;

 @Input()
 public value: string;

 @Output()
 public onInputChangeEmitter: EventEmitter<any> = new EventEmitter<any>();

 constructor() {}

 public onInputChange(e: any) {
 // when <input> is changed, emit an Object up, with what property changed, and to what new value
 this.onInputChangeEmitter.emit({ prop: this.id, newVal: e.target.value });
 }
}

import { Component, EventEmitter, Input, Output } from '@angular/core';
import angular.json eslint.config.js gojs-angular-basic.report.json LICENSE.md lint.after.all lint.after.recommended lint.before.all lint.before.recommended node_modules package.json package-lock.json README.md src tsconfig.app.json tsconfig.json as go from 'gojs';
import { CommonModule } from '@angular/common';
import { InspectorRowComponent } from './inspector-row.component';

@Component({
 selector: 'app-inspector',
 imports: [CommonModule, InspectorRowComponent],
 templateUrl: './inspector.component.html',
 styleUrls: ['./inspector.component.css'],
})
export class InspectorComponent {
 @Input()
 public nodeData: go.ObjectData;

 @Output()
 public onInspectorChange: EventEmitter<any> = new EventEmitter<any>();

 constructor() {}

 public onInputChange(propAndValObj: any) {
 this.onInspectorChange.emit(propAndValObj);
 }
}

