 header: 'Delete all favorites',
 message: 'Are you sure you want to delete all the favorites?',
 buttons: [
 {
 text: 'Cancel',
 role: 'cancel',
 cssClass: 'secondary'
 },
 {
 text: 'Okay',
 handler: () => {
 this.deleteAll();
 }
 }
 ]
 });

 await alert.present();
 }
}

