 ngOnInit() {
 console.log('YoutubeModalComponent::ngOnInit | method called');
 this.item = this.navParams.data.modalProps.item;
 }

 ngAfterViewInit() {
 this.initializeYoutubePlayerPluginWeb();
 }

 dismiss() {
 // Using the injected ModalController this page
