@Component({
 selector: 'app-page-detail',
 templateUrl: './detail.html',
 styleUrls: ['./detail.scss'],
 encapsulation: ViewEncapsulation.None
})
export class DetailComponent {
 currentYear = new Date().getFullYear();
 selectedMovie: Observable<Movie>;
 movie: Movie;

 constructor(
 private store: Store,
 private youtubeApiService: YoutubeApiService,
 private modalCtrl: ModalController,
 private activatedRoute: ActivatedRoute,
 private iziToast: IziToastService
 ) {}

 ionViewWillEnter() {
 const id = this.activatedRoute.snapshot.paramMap.get('id');
 this.getMovieDetails(id);
 }

 getMovieDetails(id: string) {
 this.selectedMovie = this.store
 .select(MovieState.movieById)
 .pipe(map((filterFn) => filterFn(id)));
 this.selectedMovie.subscribe((movie) => {
 this.movie = movie;
 });
 }

 watchTrailer() {
 console.log('DetailsPage::watchTrailer | method called');

 // Code to use Youtube Api Service: providers/youtube-api-service.ts
 this.youtubeApiService.searchMovieTrailer(this.movie.title).subscribe({
 next: (result) => {
 if (result.items.length > 0) {
 const { videoId } = result.items[0].id;
 this.movie.videoId = videoId;

 // Code to use capacitor-youtube-player plugin.
 console.log(
 'DetailsPage::watchTrailer -> platform: ' + Capacitor.platform
 );
 if (Capacitor.getPlatform() === 'web') {
 const componentProps = { modalProps: { item: this.movie } };
 this.presentModal(componentProps, YoutubeModalComponent);
 } else {
 // Native
 this.testYoutubePlayerPlugin();
 }

 /bin /run /sbin if (Capacitor.getPlatform() === 'web') {
 window.open('https://www.youtube.com/watch?v=' + videoId);
 } else { // TODO: Use capacitor-youtube-player plugin.
 window.open('https://www.youtube.com/watch?v=' + videoId, '_blank');
 }
 */
 }
 },
 error: (error) => {
 this.iziToast.show(
 'Watch Trailer',
 'Sorry, an error has occurred.',
 'red',
 'ico-error',
 'assets/avatar.png'
 );
 }
 });
 }

 async presentModal(componentProps: any, component) {
 console.log(
 'DetailsPage::presentModal | method called -> movie',
 this.movie
 );
 // const componentProps = { modalProps: { item: this.movie}};
 const modal = await this.modalCtrl.create({
 component: component,
 componentProps: componentProps
 });
 await modal.present();

 const { data } = await modal.onWillDismiss();
 if (data) {
 console.log('data', data);
 }
