import { State, Action, StateContext, Selector, NgxsOnInit } from '@ngxs/store';
import { patch, append, removeItem, updateItem } from '@ngxs/store/operators';
import { tap, catchError } from 'rxjs/operators';
import { throwError } from 'rxjs';
import { Movie } from '@models/movie.model';
import {
 FetchMovies,
 AddMovie,
 EditMovie,
 DeleteMovie,
 FilterMovies,
 SaveFilterMovies,
 GetMovieTrailer,
 ClearMovies,
 LikeMovie,
 CommentMovie,
 FavoriteMovie,
 DeleteFavoriteMovie,
 DeleteAllFavoritesMovies,
 ClearState
} from '@store/actions/movies.actions';

import { MoviesService } from '@services/movies/movies-service';
import { YoutubeApiService } from '@services/youtube-api/youtube-api-service';
import { Injectable } from '@angular/core';

export class MoviesStateModel {
 movies: Movie[];
 movieForm: {
 model: Movie;
 dirty: boolean;
 status: string;
 errors: {};
 };
 filter: {
 genre: string;
 years: {
 lower: number;
 upper: number;
 };
 rate: number;
 };
 favorites: Movie[];
}

@State<MoviesStateModel>({
 name: 'catalog',
 defaults: {
 movies: [],
 movieForm: {
 model: null,
 dirty: false,
 status: '',
 errors: {}
 },
 filter: {
 genre: 'Action',
 years: {
 lower: 1900,
 upper: new Date().getFullYear()
 },
 rate: 0
 },
 favorites: []
 }
})
@Injectable()
export class MovieState implements NgxsOnInit {
 private readonly GENRES: string[] = [
 'action',
 'comedy',
 'crime',
 'documentary',
 'drama',
 'fantasy',
 'film noir',
 'horror',
 'romance',
 'science fiction',
 'westerns',
 'animation'
 ];

 constructor(
 private moviesService: MoviesService,
 private youtubeApiService: YoutubeApiService
 ) {}

 @Selector()
 static getMovies(state: MoviesStateModel) {
 return state.movies;
 }

 @Selector()
 static movieById(state: MoviesStateModel) {
 return (id: string) => {
 return state.movies.filter((movie) => movie.id === id)[0];
 };
 }

 ngxsOnInit(ctx: StateContext<MoviesStateModel>) {
 ctx.dispatch(new ClearState());
 }

 @Action(ClearState)
 clearState({ setState }: StateContext<MoviesStateModel>) {
 setState({
 movies: [],
 movieForm: {
 model: null,
 dirty: false,
 status: '',
 errors: {}
 },
 filter: {
 genre: 'Action',
 years: {
 lower: 1900,
 upper: new Date().getFullYear()
 },
 rate: 0
 },
 favorites: []
 });
 }

 @Action(FetchMovies, { cancelUncompleted: true })
 fetchMovies(
 { getState, setState }: StateContext<MoviesStateModel>,
 { payload }
 ) {
 const { start, end } = payload;
 return this.moviesService.getMovies(start, end).pipe(
 catchError((x, caught) => {
 return throwError(() => new Error(x));
 }),
 tap({
 next: (movies) => {
 movies.forEach((movie) => {
 const genre = movie.genre.toLowerCase().split(',', 1)[0];
 if (this.GENRES.indexOf(genre) !== -1) {
 movie.genreImage = 'assets/movies-genres/' + genre + '.png';
 }
 });
 const state = getState();
 setState({
 ...state,
 movies: [...state.movies, ...movies]
 });
 },
 error: (error) => {
 console.log('error', error.message);
 }
 })
 );
 }

 @Action(AddMovie)
 addMovie({ setState }: StateContext<MoviesStateModel>, { payload }) {
 payload.poster =
 payload.poster === ''
 ? 'https://in.bmscdn.com/iedb/movies/images/website/poster/large/ela-cheppanu-et00016781-24-03-2017-18-31-40.jpg'
 : payload.poster;
 return this.moviesService.addMovie(payload).pipe(
 catchError((x, caught) => {
 return throwError(() => new Error(x));
 }),
 tap({
 next: (result) => {
 setState(
 patch({
 movies: append([result])
 })
 );
 }
 })
 );
 }

 @Action(EditMovie)
 editMovie({ setState }: StateContext<MoviesStateModel>, { payload }) {
