import {
 Component,
 ViewEncapsulation,
 OnInit,
 OnDestroy,
 NgZone
} from '@angular/core';
import { PopoverController } from '@ionic/angular';
import { Store } from '@ngxs/store';
import { Observable, Subscription } from 'rxjs';
import { FormBuilder, FormGroup, FormControl } from '@angular/forms';
import { FilterMovies, SaveFilterMovies } from '@store/actions/movies.actions';

@Component({
 standalone: true,selector: 'app-filter-movie-popover',
 templateUrl: 'filter-movie.popover.html',
 styleUrls: ['./filter-movie.popover.scss'],
 encapsulation: ViewEncapsulation.None
})
export class FilterMoviePopoverComponent implements OnInit, OnDestroy {
 range: any = {
 lower: 1900,
 upper: new Date().getFullYear()
 };

 filter$: Observable<any>;
 filterSubscription: Subscription;

 filterForm: FormGroup;

 customPopoverOptions: any = {
 header: 'Genre',
 subHeader: 'Select movie genre'
 };

 genres = [
 { id: 1, name: 'Action', image: 'assets/movies-genres/action.png' },
 { id: 2, name: 'Comedy', image: 'assets/movies-genres/comedy.png' },
 { id: 3, name: 'Crime', image: 'assets/movies-genres/crime.png' },
 {
 id: 4,
 name: 'Documentary',
 image: 'assets/movies-genres/documentary.png'
 },
 { id: 5, name: 'Drama', image: 'assets/movies-genres/drama.png' },
 { id: 6, name: 'Fantasy', image: 'assets/movies-genres/fantasy.png' },
 { id: 7, name: 'Film noir', image: 'assets/movies-genres/film noir.png' },
 { id: 8, name: 'Horror', image: 'assets/movies-genres/horror.png' },
 { id: 9, name: 'Romance', image: 'assets/movies-genres/romance.png' },
 {
 id: 10,
 name: 'Science fiction',
 image: 'assets/movies-genres/science fiction.png'
 },
 { id: 11, name: 'Westerns', image: 'assets/movies-genres/westerns.png' }
 ];

 constructor(
 private popoverCtrl: PopoverController,
 private store: Store,
 private zone: NgZone,
 private formBuilder: FormBuilder
 ) {
 this.createForm();
 }

 createForm() {
 this.filterForm = this.formBuilder.group({
 rate: new FormControl(''),
 years: new FormControl(''),
 genre: new FormControl('')
 });
 }

 ngOnInit() {
 this.filter$ = this.store.select((state) => state.catalog.filter);
 this.filterSubscription = this.filter$.subscribe((filter) => {
 this.filterForm.setValue(filter);
 if (filter.genre === '') {
 this.filterForm.patchValue({ genre: 'Action' });
 }
 });
 }

 ngOnDestroy() {
 this.filterSubscription.unsubscribe();
 }

 filterMovies() {
 console.log(this.filterForm.value);
 this.store.dispatch([
 new FilterMovies(this.filterForm.value),
 new SaveFilterMovies(this.filterForm.value)
 ]);
 this.popoverCtrl.dismiss();
 }

 clearFilterMovies() {
 this.filterForm.patchValue({
 rate: 0,
 years: { lower: 1900, upper: new Date().getFullYear() },
 genre: ''
 });
 this.store.dispatch([
 new FilterMovies(this.filterForm.value),
 new SaveFilterMovies(this.filterForm.value)
 ]);
 this.popoverCtrl.dismiss();
 }

 onRatingChange(event) {
 this.filterForm.patchValue({ rate: event.detail });
 }
}

import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';
import { timeout, retryWhen, delay } from 'rxjs/operators';
import { v4 as uuid } from 'uuid';
import { Movie } from '@models/movie.model';
import { environment } from '@environments/environment';

@Injectable()
export class MoviesService {
 private readonly URL_BASE: string = environment.API_URL_BASE;

 constructor(private http: HttpClient) {}

 getMovies(start: number, end: number): Observable<Movie[]> {
 return (
 this.http
 // Type-checking the response => .get<Movie[]>
 .get<Movie[]>(
 this.URL_BASE +
 `movies?_start=${start}&_end=${end}&_sort=year,title&_order=desc,asc`
 )
 .pipe(
 retryWhen((error) => error.pipe(delay(500))),
 timeout(5000)
 )
 );
 }

 getMovie(title: string): Observable<Movie> {
 return (
 this.http
 // Type-checking the response => .get<Movie>
 .get<Movie>(encodeURI(this.URL_BASE + `movies?title=${title}`))
 .pipe(
 retryWhen((error) => error.pipe(delay(500))),
 timeout(5000)
 )
 );
 }

 addMovie(movie: Movie): Observable<Movie> {
 movie['id'] = uuid();

 const httpOptions = {
 headers: new HttpHeaders({
 'Content-Type': 'application/json'
 })
 };

 return (
 this.http
 // Type-checking the response => .post<Movie>
 .post<Movie>(encodeURI(this.URL_BASE + `movies/`), movie, httpOptions)
 .pipe(
 retryWhen((error) => error.pipe(delay(500))),
 timeout(5000)
 )
 );
 }

 editMovie(movie: Movie): Observable<Movie> {
 const httpOptions = {
 headers: new HttpHeaders({
 'Content-Type': 'application/json'
 })
 };

 return (
 this.http
 // Type-checking the response => .post<Movie>
 .put<Movie>(
 encodeURI(this.URL_BASE + `movies/${movie['id']}`),
 movie,
 httpOptions
 )
 .pipe(
 retryWhen((error) => error.pipe(delay(500))),
 timeout(5000)
 )
 );
 }

 deleteMovie(movie: Movie): Observable<Movie> {
 return (
 this.http
 // Type-checking the response => .post<Movie>
 .delete<Movie>(encodeURI(this.URL_BASE + `movies/${movie['id']}`))
 .pipe(
 retryWhen((error) => error.pipe(delay(500))),
 timeout(5000)
 )
 );
 }

 filterMovies(filters): Observable<Movie[]> {
 const strFilters = this.checkFilters(filters);
 console.log(
 this.URL_BASE +
 `movies${strFilters}_sort=year,title&_order=desc,asc&_limit=20`
 );
 return (
 this.http
 // Type-checking the response => .get<Movie[]>
 .get<Movie[]>(
 this.URL_BASE +
 `movies${strFilters}_sort=year,title&_order=desc,asc&_limit=20`
 )
 .pipe(
 retryWhen((error) => error.pipe(delay(500))),
 timeout(5000)
 )
 );
 }

 checkFilters(filters: any) {
 let strFilters = '';
 strFilters +=
 typeof filters['genre'] !== 'undefined' && filters['genre'] !== ''
 ? `?genre=${filters.genre}&`
 : '?';
 strFilters +=
 typeof filters['years'] !== 'undefined'
 ? `year_gte=${filters.years.lower}&year_lte=${filters.years.upper}&`
 : '';
 strFilters +=
 typeof filters['rate'] !== 'undefined' && filters['rate'] !== 0
 ? `rate=${filters.rate}&`
 : '';
 return strFilters;
 }

 searchMovies(queryText: string): Observable<Movie[]> {
 return (
 this.http
 // Type-checking the response => .get<Movie[]>
 .get<Movie[]>(this.URL_BASE + `movies?q=${queryText}&_limit=20`)
 .pipe(
 retryWhen((error) => error.pipe(delay(500))),
 timeout(5000)
 )
 );
 }
}

