import {
 DestroyRef,
 Directive,
 inject,
 Input,
 OnInit,
 TemplateRef,
 ViewContainerRef,
} from "@angular/core";
import { UserService } from "./services/user.service";
import { takeUntilDestroyed } from "@angular/core/rxjs-interop";

@Directive({
 selector: "[ifAuthenticated]",
 standalone: true,
})
export class IfAuthenticatedDirective<T> implements OnInit {
 destroyRef = inject(DestroyRef);
 constructor(
 private templateRef: TemplateRef<T>,
 private userService: UserService,
 private viewContainer: ViewContainerRef,
 ) {}

 condition: boolean = false;
 hasView = false;

 ngOnInit() {
 this.userService.isAuthenticated
 .pipe(takeUntilDestroyed(this.destroyRef))
 .subscribe((isAuthenticated: boolean) => {
 const authRequired = isAuthenticated && this.condition;
 const unauthRequired = !isAuthenticated && !this.condition;

 if ((authRequired || unauthRequired) && !this.hasView) {
 this.viewContainer.createEmbeddedView(this.templateRef);
 this.hasView = true;
 } else if (this.hasView) {
 this.viewContainer.clear();
 this.hasView = false;
 }
 });
 }

 @Input() set ifAuthenticated(condition: boolean) {
 this.condition = condition;
 }
}

import {
 Component,
 DestroyRef,
 EventEmitter,
 inject,
 Input,
 Output,
} from "@angular/core";
import { Router } from "@angular/router";
import { EMPTY, switchMap } from "rxjs";
import { NgClass } from "@angular/common";
import { ArticlesService } from "../services/articles.service";
import { UserService } from "../../../core/auth/services/user.service";
import { Article } from "../models/article.model";
import { takeUntilDestroyed } from "@angular/core/rxjs-interop";

@Component({
 selector: "app-favorite-button",
 template: `
 <button
 class="btn btn-sm"
 [ngClass]="{
 disabled: isSubmitting,
 'btn-outline-primary': !article.favorited,
 'btn-primary': article.favorited
 }"
 (click)="toggleFavorite()"
 >
 <i class="ion-heart"></i> <ng-content></ng-content>
 </button>
 `,
 imports: [NgClass],
 standalone: true,
})
export class FavoriteButtonComponent {
 destroyRef = inject(DestroyRef);
 isSubmitting = false;

 @Input() article!: Article;
 @Output() toggle = new EventEmitter<boolean>();

 constructor(
 private readonly articleService: ArticlesService,
 private readonly router: Router,
 private readonly userService: UserService,
 ) {}

 toggleFavorite(): void {
 this.isSubmitting = true;

 this.userService.isAuthenticated
 .pipe(
 switchMap((authenticated) => {
 if (!authenticated) {
 void this.router.navigate(["/register"]);
 return EMPTY;
 }

 if (!this.article.favorited) {
 return this.articleService.favorite(this.article.slug);
 } else {
 return this.articleService.unfavorite(this.article.slug);
 }
 }),
 takeUntilDestroyed(this.destroyRef),
 )
 .subscribe({
 next: () => {
 this.isSubmitting = false;
 this.toggle.emit(!this.article.favorited);
 },
