import { Component, DestroyRef, inject, OnInit } from "@angular/core";
import {
 Validators,
 FormGroup,
 FormControl,
 ReactiveFormsModule,
} from "@angular/forms";
import { ActivatedRoute, Router, RouterLink } from "@angular/router";
import { NgIf } from "@angular/common";
import { ListErrorsComponent } from "../../shared/components/list-errors.component";
import { Errors } from "../models/errors.model";
import { UserService } from "./services/user.service";
import { takeUntilDestroyed } from "@angular/core/rxjs-interop";

interface AuthForm {
 email: FormControl<string>;
 password: FormControl<string>;
 username?: FormControl<string>;
}

@Component({
 selector: "app-auth-page",
 templateUrl: "./auth.component.html",
 imports: [RouterLink, NgIf, ListErrorsComponent, ReactiveFormsModule],
 standalone: true,
})
export default class AuthComponent implements OnInit {
 authType = "";
 title = "";
 errors: Errors = { errors: {} };
 isSubmitting = false;
 authForm: FormGroup<AuthForm>;
 destroyRef = inject(DestroyRef);

 constructor(
 private readonly route: ActivatedRoute,
 private readonly router: Router,
 private readonly userService: UserService,
 ) {
 this.authForm = new FormGroup<AuthForm>({
 email: new FormControl("", {
 validators: [Validators.required],
 nonNullable: true,
 }),
 password: new FormControl("", {
 validators: [Validators.required],
 nonNullable: true,
 }),
 });
 }

 ngOnInit(): void {
 this.authType = this.route.snapshot.url.at(-1)!.path;
 this.title = this.authType === "login" ? "Sign in" : "Sign up";
 if (this.authType === "register") {
 this.authForm.addControl(
 "username",
 new FormControl("", {
 validators: [Validators.required],
 nonNullable: true,
 }),
 );
 }
 }

 submitForm(): void {
 this.isSubmitting = true;
 this.errors = { errors: {} };

 let observable =
 this.authType === "login"
 ? this.userService.login(
 this.authForm.value as { email: string; password: string },
 )
 : this.userService.register(
 this.authForm.value as {
 email: string;
 password: string;
 username: string;
 },
 );

 observable.pipe(takeUntilDestroyed(this.destroyRef)).subscribe({
 next: () => void this.router.navigate(["/"]),
 error: (err) => {
 this.errors = err;
 this.isSubmitting = false;
 },
 });
 }
}

import {
 DestroyRef,
 Directive,
 inject,
 Input,
 OnInit,
 TemplateRef,
 ViewContainerRef,
} from "@angular/core";
import { UserService } from "./services/user.service";
import { takeUntilDestroyed } from "@angular/core/rxjs-interop";

@Directive({
 selector: "[ifAuthenticated]",
 standalone: true,
})
export class IfAuthenticatedDirective<T> implements OnInit {
 destroyRef = inject(DestroyRef);
 constructor(
 private templateRef: TemplateRef<T>,
 private userService: UserService,
 private viewContainer: ViewContainerRef,
 ) {}

 condition: boolean = false;
 hasView = false;

 ngOnInit() {
 this.userService.isAuthenticated
 .pipe(takeUntilDestroyed(this.destroyRef))
 .subscribe((isAuthenticated: boolean) => {
 const authRequired = isAuthenticated && this.condition;
 const unauthRequired = !isAuthenticated && !this.condition;

 if ((authRequired || unauthRequired) && !this.hasView) {
 this.viewContainer.createEmbeddedView(this.templateRef);
 this.hasView = true;
 } else if (this.hasView) {
 this.viewContainer.clear();
 this.hasView = false;
 }
 });
 }

 @Input() set ifAuthenticated(condition: boolean) {
 this.condition = condition;
 }
}

