@Component({
 selector: "app-article-list",
 template: `
 @if (loading === LoadingState.LOADING) {
 <div class="article-preview">Loading articles...</div>
 }

 @if (loading === LoadingState.LOADED) {
 @for (article of results; track article.slug) {
 <app-article-preview [article]="article" />
 } @empty {
 <div class="article-preview">No articles are here... yet.</div>
 }

 <nav>
 <ul class="pagination">
 @for (pageNumber of totalPages; track pageNumber) {
 <li
 class="page-item"
 [ngClass]="{ active: pageNumber === currentPage }"
 >
 <button class="page-link" (click)="setPageTo(pageNumber)">
 {{ pageNumber }}
 </button>
 </li>
 }
 </ul>
 </nav>
 }
 `,
 imports: [ArticlePreviewComponent, NgForOf, NgClass, NgIf],
 styles: `
 .page-link {
 cursor: pointer;
 }
 `,
 standalone: true,
})
export class ArticleListComponent {
 query!: ArticleListConfig;
 results: Article[] = [];
 currentPage = 1;
 totalPages: Array<number> = [];
 loading = LoadingState.NOT_LOADED;
 LoadingState = LoadingState;
 destroyRef = inject(DestroyRef);

 @Input() limit!: number;
 @Input()
 set config(config: ArticleListConfig) {
 if (config) {
 this.query = config;
 this.currentPage = 1;
 this.runQuery();
 }
 }

 constructor(private articlesService: ArticlesService) {}

 setPageTo(pageNumber: number) {
 this.currentPage = pageNumber;
 this.runQuery();
 }

 runQuery() {
 this.loading = LoadingState.LOADING;
 this.results = [];

 // Create limit and offset filter (if necessary)
 if (this.limit) {
 this.query.filters.limit = this.limit;
 this.query.filters.offset = this.limit angular.json angular-realworld-example-app.report.json CODE_OF_CONDUCT.md eslint.config.js LICENSE lint.after.all lint.after.recommended lint.before.all lint.before.recommended logo.png node_modules package.json package-lock.json README.md src tsconfig.app.json tsconfig.json tsconfig.spec.json (this.currentPage - 1);
 }

 this.articlesService
 .query(this.query)
 .pipe(takeUntilDestroyed(this.destroyRef))
 .subscribe((data) => {
 this.loading = LoadingState.LOADED;
 this.results = data.articles;

 // Used from http://www.jstips.co/en/create-range-0...n-easily-using-one-line/
 this.totalPages = Array.from(
 new Array(Math.ceil(data.articlesCount / this.limit)),
 (val, index) => index + 1,
 );
 });
 }
}

