 readonly router = inject(Router);
 readonly Theme = Theme;

 isUserLoggedIn = this.authenticationService.isUserLoggedIn();
 themeSelected = this.themeManagerService.getThemeFromLocalStorageValue();

 constructor() {
 effect(() => {
