 }

 toggleTheme() {
 this.themeSelected =
 // eslint-disable-next-line @typescript-eslint/prefer-nullish-coalescing
 this.themeManagerService.getThemeFromLocalStorageValue() === Theme.DARK ||
 this.themeSelected === null
 ? Theme.LIGHT
 : Theme.DARK;
