import { Injectable, Renderer2, RendererFactory2 } from '@angular/core';

enum AlertType {
 SUCCESS = 'success',
 ERROR = 'error',
}

type Alert = {
 message: string;
 type: AlertType;
 hasCountdown?: boolean;
 duration?: number;
};

@Injectable({
 providedIn: 'root',
})
export class AlertService {
 private readonly renderer: Renderer2;

 constructor(rendererFactory: RendererFactory2) {
 this.renderer = rendererFactory.createRenderer(null, null);
 }

 createSuccessAlert(message: string) {
 this.createAlert({ message, type: AlertType.SUCCESS, duration: 7000, hasCountdown: true });
 }

 createErrorAlert(message: string) {
 this.createAlert({ message, type: AlertType.ERROR });
 }

 private createAlert(alert: Alert): void {
 const alertElement = this.createAlertElement(alert);
 const container = document.body;
 this.renderer.appendChild(container, alertElement);
 alertElement.toast();
 }

 private createAlertElement(alert: Alert): HTMLElement & { toast: () => void } {
 const alertElement = this.renderer.createElement('sl-alert');
 alertElement.classList.add(`alert--${alert.type}`);
 this.renderer.setAttribute(alertElement, 'closable', '');
 this.renderer.setAttribute(alertElement, 'variant', alert.type);
 if (alert.duration) {
 this.renderer.setAttribute(alertElement, 'duration', alert.duration.toString());
 }
 if (alert.hasCountdown) {
 this.renderer.setAttribute(alertElement, 'countdown', 'rtl');
 }
 this.renderer.setProperty(alertElement, 'innerHTML', alert.message);
 return alertElement as HTMLElement & { toast: () => void };
 }
}

import { inject, Injectable } from '@angular/core';
import { LOCAL_STORAGE } from '~core/providers/local-storage';

declare const window: Window &
 // eslint-disable-next-line @typescript-eslint/max-params
 typeof globalThis & { gtag?: (a: string, b: string, o: object) => void };

const CONSENT_COOKIE_KEY = 'isCookiesConsentAccepted';
const CONSENT_COOKIE_VALUE = 'true';

export enum ConsentState {
 DENIED = 'denied',
 GRANTED = 'granted',
}

@Injectable({
 providedIn: 'root',
})
export class CookieConsentService {
 private readonly localStorage: Storage | null = inject(LOCAL_STORAGE);

 setCookieConsent(state: ConsentState): boolean {
 if (!this.setConsentInLocalStorage()) {
 return false;
 }
 return this.updateGtagConsent(state);
 }

 getCookieState(): boolean {
 return this.localStorage?.getItem(CONSENT_COOKIE_KEY) === CONSENT_COOKIE_VALUE;
 }

 private setConsentInLocalStorage(): boolean {
 try {
 this.localStorage?.setItem(CONSENT_COOKIE_KEY, CONSENT_COOKIE_VALUE);
 return true;
 } catch {
 return false;
 }
 }

 private updateGtagConsent(state: ConsentState): boolean {
 try {
 if (window.gtag) {
 const consentOptions = {

 ad_user_data: state,
 ad_personalization: state,
 ad_storage: state,
 analytics_storage: state,
 };

 if (state === ConsentState.DENIED) {
 window.gtag('consent', 'default', {
 ...consentOptions,
 wait_for_update: 500,

 });
 } else {
 window.gtag('consent', 'update', {
 ...consentOptions,
 });
 }
 }
 return true;
 } catch {
 return false;
 }
 }
}

