 trigger('flyInOut', [
 state('inactive', style({ opacity: 0 })),
 transition(
 'inactive => active',
 animate(
 '300ms ease-out',
 keyframes([
 style({
 opacity: 0,
 bottom: '-15px',
 'max-height': 0,
 'max-width': 0,
 'margin-top': 0,
 }),
 style({
 opacity: 0.8,
 bottom: '-3px',
 }),
 style({
 opacity: 1,
 bottom: '0',
 'max-height': '200px',
 'margin-top': '12px',
 'max-width': '400px',
 }),
