 ),
 ),
 state(
 'active',
 style({
 bottom: '0',
 'max-height': '200px',
 'margin-top': '12px',
 'max-width': '400px',
 }),
 ),
 transition(
 'active => removed',
 animate(
 '300ms ease-out',
 keyframes([
 style({
 opacity: 1,
 transform: 'translateY(0)'
 }),
 style({
 opacity: 0,
 transform: 'translateY(25%)'
 }),
 ]),
 ),
 ),
 ]),
 ],
})
export class NotyfToast extends Toast {}

