 title: this.title,
 transform: parsedTransform,
 classes: faClassList(classOpts),
 mask: this.mask != null ? this.findIconDefinition(this.mask) : null,
 symbol: this.symbol,
 attributes: {
 role: this.a11yRole,
 },
 styles,
 };
 }
