 addDaysWithExclusions(
 this.dateAdapter,
 this.viewDate,
 -this.daysInWeek,
 this.excludeDays
 )
 );
 } else {
 this.viewDateChange.emit(subFn(this.viewDate, 1));
 }
 }
}

