 {
 start: setHours(setMinutes(new Date(), 20), 15),
 end: setHours(setMinutes(new Date(), 40), 17),
 title: 'An event',
 resizable: {
 afterEnd: true,
 },
 draggable: true,
 },
 ];

 refresh = new Subject<void>();

 eventTimesChanged({
 event,
 newStart,
 newEnd,
 }: CalendarEventTimesChangedEvent): void {
 event.start = newStart;
 event.end = newEnd;
 this.refresh.next();
 }
}

