 eventDropped({
 event,
 newStart,
 newEnd,
 allDay,
 }: CalendarEventTimesChangedEvent): void {
 const externalIndex = this.externalEvents.indexOf(event);
 if (typeof allDay !== 'undefined') {
 event.allDay = allDay;
 }
 if (externalIndex > -1) {
 this.externalEvents.splice(externalIndex, 1);
 this.events.push(event);
