// get your own key from https://holidayapi.com/
const HOLIDAY_API_KEY = '8eb2582d-3a4c-4fc5-94c8-3e21487d4e23';

// change this to your own country
const COUNTRY_CODE = 'US';

interface Holiday {
 date: string;
 name: string;
}

type CalendarEventWithMeta = CalendarEvent<
 { type: 'holiday'; holiday: Holiday } | { type: 'normal' }
>;

@Component({
 selector: 'mwl-demo-component',
 changeDetection: ChangeDetectionStrategy.OnPush,
 templateUrl: 'template.html',
})
export class DemoComponent implements OnInit {
 view: CalendarView = CalendarView.Month;

 viewDate = startOfYear(subYears(new Date(), 1));

 events: CalendarEventWithMeta[] = [];

 constructor(private http: HttpClient, private cdr: ChangeDetectorRef) {}

 ngOnInit(): void {
 this.fetchHolidays();
 }

 private fetchHolidays() {
 this.http
 .get<{ holidays: Holiday[] }>('https://holidayapi.com/v1/holidays', {
 params: {
 country: COUNTRY_CODE,
 year: String(new Date().getFullYear() - 1),
 key: HOLIDAY_API_KEY,
 },
 })
 .subscribe(({ holidays }) => {
 this.events = holidays.map((holiday) => {
 return {
 start: new Date(holiday.date),
 title: holiday.name,
 allDay: true,
 meta: {
 type: 'holiday',
 holiday,
 },
 };
 });
 this.cdr.markForCheck();
 });
 }
}

import {
 Component,
 ChangeDetectionStrategy,
 ChangeDetectorRef,
} from '@angular/core';
// As an alternative to rrule there is also rSchedule
// See https://github.com/mattlewis92/angular-calendar/issues/711#issuecomment-418537158 for more info
import { RRule } from 'rrule';
import moment from 'moment-timezone';
import {
 CalendarDayViewBeforeRenderEvent,
 CalendarEvent,
 CalendarMonthViewBeforeRenderEvent,
 CalendarView,
 CalendarWeekViewBeforeRenderEvent,
} from 'angular-calendar';
import { colors } from '../demo-utils/colors';
import { ViewPeriod } from 'calendar-utils';

interface RecurringEvent {
 title: string;
 color: any;
 rrule?: {
 freq: any;
 bymonth?: number;
 bymonthday?: number;
 byweekday?: any;
 };
}

// we set the timezone to UTC to avoid issues with DST changes
// see https://github.com/mattlewis92/angular-calendar/issues/717 for more info
moment.tz.setDefault('Utc');

@Component({
 selector: 'mwl-demo-component',
 changeDetection: ChangeDetectionStrategy.OnPush,
 templateUrl: 'template.html',
})
export class DemoComponent {
 view: CalendarView = CalendarView.Month;

 viewDate = moment().toDate();

 recurringEvents: RecurringEvent[] = [
 {
 title: 'Recurs on the 5th of each month',
 color: colors.yellow,
 rrule: {
 freq: RRule.MONTHLY,
 bymonthday: 5,
 },
 },
 {
 title: 'Recurs yearly on the 10th of the current month',
 color: colors.blue,
 rrule: {
 freq: RRule.YEARLY,
 bymonth: moment().month() + 1,
 bymonthday: 10,
 },
 },
 {
 title: 'Recurs weekly on mondays',
 color: colors.red,
 rrule: {
 freq: RRule.WEEKLY,
 byweekday: [RRule.MO],
 },
 },
 ];

 calendarEvents: CalendarEvent[] = [];

 viewPeriod: ViewPeriod;

 constructor(private cdr: ChangeDetectorRef) {}

 updateCalendarEvents(
 viewRender:
 | CalendarMonthViewBeforeRenderEvent
 | CalendarWeekViewBeforeRenderEvent
 | CalendarDayViewBeforeRenderEvent
 ): void {
 if (
 !this.viewPeriod ||
 !moment(this.viewPeriod.start).isSame(viewRender.period.start) ||
 !moment(this.viewPeriod.end).isSame(viewRender.period.end)
 ) {
 this.viewPeriod = viewRender.period;
 this.calendarEvents = [];

 this.recurringEvents.forEach((event) => {
 const rule: RRule = new RRule({
 ...event.rrule,
 dtstart: moment(viewRender.period.start).startOf('day').toDate(),
 until: moment(viewRender.period.end).endOf('day').toDate(),
 });
 const { title, color } = event;

 rule.all().forEach((date) => {
 this.calendarEvents.push({
 title,
 color,
 start: moment(date).toDate(),
 });
 });
 });
 this.cdr.detectChanges();
 }
 }
}

