import {
 Component,
 Input,
 Output,
 EventEmitter,
 TemplateRef,
} from '@angular/core';
import { MonthViewDay, CalendarEvent } from 'calendar-utils';
import { isWithinThreshold, trackByEventId } from '../../../common/util/util';
import { PlacementArray } from 'positioning';

@Component({
 selector: 'mwl-calendar-month-cell',
 template: `
 <ng-template
 #defaultTemplate
 let-day="day"
 let-openDay="openDay"
 let-locale="locale"
 let-tooltipPlacement="tooltipPlacement"
 let-highlightDay="highlightDay"
 let-unhighlightDay="unhighlightDay"
 let-eventClicked="eventClicked"
 let-tooltipTemplate="tooltipTemplate"
 let-tooltipAppendToBody="tooltipAppendToBody"
 let-tooltipDelay="tooltipDelay"
 let-trackByEventId="trackByEventId"
 let-validateDrag="validateDrag"
 >
 <div
 class="cal-cell-top"
 [attr.aria-label]="
 { day: day, locale: locale } | calendarA11y : 'monthCell'
 "
 >
 <span aria-hidden="true">
 <span class="cal-day-badge" *ngIf="day.badgeTotal > 0">{{
 day.badgeTotal
 }}</span>
 <span class="cal-day-number">{{
 day.date | calendarDate : 'monthViewDayNumber' : locale
 }}</span>
 </span>
 </div>
 <div class="cal-events" *ngIf="day.events.length > 0">
 <div
 class="cal-event"
 *ngFor="let event of day.events; trackBy: trackByEventId"
 [ngStyle]="{ backgroundColor: event.color?.primary }"
 [ngClass]="event?.cssClass"
 (mouseenter)="highlightDay.emit({ event: event })"
 (mouseleave)="unhighlightDay.emit({ event: event })"
 [mwlCalendarTooltip]="
 event.title | calendarEventTitle : 'monthTooltip' : event
 "
 [tooltipPlacement]="tooltipPlacement"
 [tooltipEvent]="event"
 [tooltipTemplate]="tooltipTemplate"
 [tooltipAppendToBody]="tooltipAppendToBody"
 [tooltipDelay]="tooltipDelay"
 mwlDraggable
 [class.cal-draggable]="event.draggable"
 dragActiveClass="cal-drag-active"
 [dropData]="{ event: event, draggedFrom: day }"
 [dragAxis]="{ x: event.draggable, y: event.draggable }"
 [validateDrag]="validateDrag"
 [touchStartLongPress]="{ delay: 300, delta: 30 }"
 (mwlClick)="eventClicked.emit({ event: event, sourceEvent: $event })"
 [attr.aria-hidden]="{} | calendarA11y : 'hideMonthCellEvents'"
 ></div>
 </div>
 </ng-template>
 <ng-template
 [ngTemplateOutlet]="customTemplate || defaultTemplate"
 [ngTemplateOutletContext]="{
 day: day,
 openDay: openDay,
 locale: locale,
 tooltipPlacement: tooltipPlacement,
 highlightDay: highlightDay,
 unhighlightDay: unhighlightDay,
 eventClicked: eventClicked,
 tooltipTemplate: tooltipTemplate,
 tooltipAppendToBody: tooltipAppendToBody,
 tooltipDelay: tooltipDelay,
 trackByEventId: trackByEventId,
 validateDrag: validateDrag
 }"
 >
 </ng-template>
 `,
 // eslint-disable-next-line @angular-eslint/no-host-metadata-property
 host: {
 class: 'cal-cell cal-day-cell',
 '[class.cal-past]': 'day.isPast',
 '[class.cal-today]': 'day.isToday',
 '[class.cal-future]': 'day.isFuture',
 '[class.cal-weekend]': 'day.isWeekend',
 '[class.cal-in-month]': 'day.inMonth',
 '[class.cal-out-month]': '!day.inMonth',
 '[class.cal-has-events]': 'day.events.length > 0',
 '[class.cal-open]': 'day === openDay',
 '[class.cal-event-highlight]': '!!day.backgroundColor',
 },
})
export class CalendarMonthCellComponent {
 @Input() day: MonthViewDay;

 @Input() openDay: MonthViewDay;

 @Input() locale: string;

 @Input() tooltipPlacement: PlacementArray;

 @Input() tooltipAppendToBody: boolean;

 @Input() customTemplate: TemplateRef<any>;

 @Input() tooltipTemplate: TemplateRef<any>;

 @Input() tooltipDelay: number | null;

 @Output() highlightDay: EventEmitter<any> = new EventEmitter();

 @Output() unhighlightDay: EventEmitter<any> = new EventEmitter();

 @Output() eventClicked = new EventEmitter<{
 event: CalendarEvent;
 sourceEvent: MouseEvent;
 }>();

 trackByEventId = trackByEventId;

 validateDrag = isWithinThreshold;
}

