import {
 Component,
 Input,
 TemplateRef,
 EventEmitter,
 Output,
} from '@angular/core';
import { WeekDay } from 'calendar-utils';
import { trackByWeekDayHeaderDate } from '../../../common/util/util';

@Component({
 selector: 'mwl-calendar-month-view-header',
 template: `
 <ng-template
 #defaultTemplate
 let-days="days"
 let-locale="locale"
 let-trackByWeekDayHeaderDate="trackByWeekDayHeaderDate"
 >
 <div class="cal-cell-row cal-header" role="row">
 <div
 class="cal-cell"
 *ngFor="let day of days; trackBy: trackByWeekDayHeaderDate"
 [class.cal-past]="day.isPast"
 [class.cal-today]="day.isToday"
 [class.cal-future]="day.isFuture"
 [class.cal-weekend]="day.isWeekend"
 (click)="
 columnHeaderClicked.emit({
 isoDayNumber: day.day,
 sourceEvent: $event
 })
 "
 [ngClass]="day.cssClass"
 tabindex="0"
 role="columnheader"
 >
 {{ day.date | calendarDate : 'monthViewColumnHeader' : locale }}
 </div>
 </div>
 </ng-template>
 <ng-template
 [ngTemplateOutlet]="customTemplate || defaultTemplate"
 [ngTemplateOutletContext]="{
 days: days,
 locale: locale,
 trackByWeekDayHeaderDate: trackByWeekDayHeaderDate
 }"
 >
 </ng-template>
 `,
})
export class CalendarMonthViewHeaderComponent {
 @Input() days: WeekDay[];

 @Input() locale: string;

 @Input() customTemplate: TemplateRef<any>;

 @Output() columnHeaderClicked = new EventEmitter<{
 isoDayNumber: number;
 sourceEvent: MouseEvent;
 }>();

 trackByWeekDayHeaderDate = trackByWeekDayHeaderDate;
}

